import { useEffect, useState } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const useLeafletMap = (center, zoom) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (map) {
      // Пример добавления маркера
      L.marker(center).addTo(map).bindPopup('Привет, OpenStreetMap!');
    }
  }, [map, center]);

  return (
    <MapContainer center={center} zoom={zoom} style={{ height, width: '100%' }} whenCreated={setMap}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={center}>
        <Popup>Привет, OpenStreetMap!</Popup>
      </Marker>
    </MapContainer>
  );
};

export default useLeafletMap;
