import { useEffect, useState } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

// Создайте свою кастомную иконку
const customIcon = new L.Icon({
  iconUrl: 'path/to/your/custom-icon.png', // Укажите путь к вашему изображению
  iconSize: [25, 41], // Размер иконки
  iconAnchor: [12, 41], // Точка привязки иконки
  popupAnchor: [1, -34], // Точка привязки всплывающего окна

  shadowSize: [41, 41], // Размер тени
  shadowAnchor: [12, 41] // Точка привязки тени
});

const useLeafletMap = (center, zoom, height, width, markers = [], imageUrl, imageBounds) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (map) {
      map.eachLayer(layer => {
        if (layer instanceof L.Marker) {
          map.removeLayer(layer);
        }
      });

      markers.forEach(({ position, popup }) => {
        L.marker(position, { icon: customIcon }).addTo(map).bindPopup(popup);
      });

      if (imageUrl && imageBounds) {
        L.imageOverlay(imageUrl, imageBounds).addTo(map);
        map.fitBounds(imageBounds);
      }
    }
  }, [map, markers, imageUrl, imageBounds]);

  return (
    <MapContainer center={center} zoom={zoom} style={{ height: height, width: width }} whenCreated={setMap}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution={false}
      />
      {markers.map((marker, index) => (
        <Marker key={index} position={marker.position} icon={customIcon}>
          <Popup>{marker.popup}</Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default useLeafletMap;
