import React from 'react';
import styles from './Modal.module.css';
import FormForCall from '../Forms/FormForCall';
import FormForMessage from '../Forms/FormForMessage';
import { useDispatch, useSelector } from 'react-redux';

const Modal = ({ onClose, children }) => {
    const dispatch = useDispatch();
    const changeState = useSelector(state => state.changeColorState)
    return (
        <div className={styles.zModalOverlay} onClick={onClose}>
            <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
                <button className={styles.closeButton} onClick={onClose}>
                    <box-icon name='x' color="#000" />
                </button>

                <p className="textBlackBig">ЗАПИСЬ</p>
                <div className={styles.tabContainer}>
                    <button className={`${styles.tabButton} ${styles.activeTab}`}>ЗАКАЗАТЬ ЗВОНОК</button>
                    <button className={styles.tabButton}>НАПИСАТЬ НАМ</button>
                </div>
                {children}

                <FormForCall/>

                <FormForMessage/>
                
                <button type="submit" className={styles.submitButton}><span className='textWhiteSmall'>ОТПРАВИТЬ ЗАЯВКУ</span></button>
            </div>
        </div>
    );
};

export default Modal;
