import { useEffect, useState } from 'react';
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

const useLeafletMap = (center, zoom, height, width, markers = [], imageUrl, imageBounds) => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    if (map) {
      // Убедитесь, что иконка создается заново при каждом изменении imgIcon
      const customIcon = new L.Icon({
        iconUrl: imageUrl, // Укажите путь к вашему изображению
      });

      map.eachLayer(layer => {
        if (layer instanceof L.Marker) {
          map.removeLayer(layer);
        }
      });

      markers.forEach(({ position, popup }) => {
        L.marker(position, { icon: customIcon }).addTo(map).bindPopup(popup);
      });
    }
  }, [map, markers, imageUrl, imageBounds]); // Добавьте imgIcon в зависимости

  return (
    <MapContainer center={center} zoom={zoom} style={{ height: height, width: width }} whenCreated={setMap}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution={false}
      />
      {markers.map((marker, index) => (
        <Marker key={index} position={marker.position} icon={new L.Icon({ iconUrl: imageUrl })}> {/* Исправлено */}
          <Popup>{marker.popup}</Popup>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default useLeafletMap;
