import React, { useState, useEffect } from "react";

const HeavyTextGray = (props) => {
    const initialFontSize = parseInt(props.fontSize, 10) || 30;
    const initialMargin = parseInt(props.margin, 10) || 0;

    const [fontSize, setFontSize] = useState(initialFontSize);
    const [margin, setMargin] = useState(initialMargin);
    const color = props.color || "white";
    const className = props.className || "HeavyTextGray";

    const adjustSizes = (width) => {
        if (width >= 2500) {
            const diff = Math.floor((width - 2500) / 100);
            setFontSize(initialFontSize + diff);
            setMargin(initialMargin + diff * 0.2); // Увеличение отступа не так значительно, как размера шрифта
        } else {
            const diff = Math.floor((2500 - width) / 100);
            setFontSize(initialFontSize - diff);
            setMargin(initialMargin - diff * 0.2); // Уменьшение отступа не так значительно, как размера шрифта
        }
    };

    useEffect(() => {
        const handleResize = () => {
            adjustSizes(window.innerWidth);
        };

        handleResize();
        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, [initialFontSize, initialMargin]);

    return (
        <p
            className={className}
            style={{
                fontSize: {fontSize},
                color: color,
                margin: {margin}
            }}
        >
            {props.text}
        </p>
    );
}

export default HeavyTextGray;
