import React from 'react';
import styles from './Modal.module.css';
import { Link } from 'react-router-dom/cjs/react-router-dom.min';

const Modal = ({ onClose, children, display }) => {
    return (
        <div className={styles.zModalOverlay} onClick={onClose}>
            <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
                <button className={styles.closeButton} onClick={onClose}>
                    <box-icon name='x' color="#000" />
                </button>

                <p className="textBlackBig">ЗАПИСЬ</p>
                <div className={styles.tabContainer}>
                    <button className={`${styles.tabButton} ${styles.activeTab}`}>ЗАКАЗАТЬ ЗВОНОК</button>
                    <button className={styles.tabButton}>НАПИСАТЬ НАМ</button>
                </div>
                {children}

                {/* <form className={styles.form} style={{display: display}}>
                    <input placeholder="ВВЕДИТЕ ИМЯ" type="text" name="text" className="input" />
                    <input placeholder="ВВЕДИТЕ НОМЕР ТЕЛЕФОНА *" type="text" name="text" className="input" />
                    <div className={styles.privacyContainer}>
                        <input type="checkbox" id="privacy" className={styles.checkbox} />
                        <label htmlFor="privacy" className={styles.privacyText}>согласен с <Link><span className={styles.spanColor}>политикой конфиденциальности</span></Link></label>
                    </div>
                    <button type="submit" className={styles.submitButton}><span className='textWhiteSmall'>ОТПРАВИТЬ ЗАЯВКУ</span></button>
                </form> */}


            </div>
        </div>
    );
};

export default Modal;
