import { Link } from "react-router-dom";
import { useRef, useState, useEffect } from "react";
import ErrorPopup from "../ErrorGroup/ErrorPopup";
import SuccessPopup from "../ErrorGroup/SuccessPopup";
import useValidForm from "../../../Hooks/useValidForm";
import useHttp from "../../../Hooks/useHttp";
import styles from "./FormForMessage.module.css";

const FormForMessage = ({ display }) => {

    const [errors, setErrors] = useState([]);
    const [successMessage, setSuccessMessage] = useState("");
    const [isChecked, setIsChecked] = useState(false);
    const [errorStateForTg, setErrorStateForTg] = useState(undefined);

    const refName = useRef();
    const refPhone = useRef();
    const refEmail = useRef();
    const refText = useRef();

    const [refNameValue, setRefNameValue] = useState("");
    const [refPhoneValue, setRefPhoneValue] = useState("");
    const [refEmailValue, setRefEmailValue] = useState("");
    const [refTextValue, setRefTextValue] = useState("");

    useEffect(() => {
        if (refName.current) {
            setRefNameValue(refName.current.value);
        }

        if (refPhone.current) {
            setRefPhoneValue(refPhone.current.value);
        }

        if (refEmail.current) {
            setRefEmailValue(refEmail.current.value);
        }
        if (refText.current) {
            setRefTextValue(refText.current.value);
        }
    }, [refName.current?.value, refPhone.current?.value, refEmail.current?.value, refText.current?.value]);

    const handleCheckboxChange = (e) => {
        setIsChecked(e.target.checked);
    };

    const sentDataHandler = (e) => {
        e.preventDefault();
        const newErrors = [];

        if (!isPhoneNumberValid) {
            newErrors.push("Укажите номер телефона");
        }

        if (!isChecked) {
            newErrors.push("Вы должны согласиться с политикой конфиденциальности");
        }

        if (newErrors.length > 0) {
            setErrors(newErrors);
            setSuccessMessage("");
        } else {
            setErrors([]);
            setSuccessMessage("Ожидайте, мы вам перезвоним");
            console.log(`Имя: ${refNameValue}, Номер телефона: ${refPhoneValue}`);
            sentDataToTelegramHandler();
        }
    };




    return (
        <>
            <form className={styles.form} style={{ display: display }}>
                <input placeholder="ВВЕДИТЕ ИМЯ" type="text" name="text" className="input" ref={refName}/>
                <input placeholder="ВВЕДИТЕ НОМЕР ТЕЛЕФОНА *" type="text" name="text" className="input" ref={refPhone}/>
                <input placeholder="ВВЕДИТЕ EMAIL *" type="text" name="text" className="input" ref={refEmail}/>
                <textarea placeholder="ВВЕДИТЕ ТЕКСТ *" type="text" name="message" className="input" style={{ height: "100px" }} ref={refText}/>

                <div className={styles.privacyContainer}>
                    <input type="checkbox" id="privacy" className={styles.checkbox} />
                    <label htmlFor="privacy" className={styles.privacyText}>согласен с <Link>
                        <span className={styles.spanColor}>политикой конфиденциальности *</span>
                    </Link></label>
                </div>

                <button 
                type="submit" 
                className={styles.submitButton} 
                disabled={errorStateForTg === false} >
                    <span className='textWhiteSmall'>ОТПРАВИТЬ ЗАЯВКУ</span>
                </button>

            </form>
        </>
    )
}

export default FormForMessage;
