import React from "react";
import { useDispatch, useSelector } from "react-redux";
import styles from "./Enrol.module.css";
import { entrySliceClose, entrySliceOpen } from "../../../Store/Slices/entrySlice";
import Modal from "../Modal/Modal";

const Enrol = (props) => {
    const openModalWindow = useSelector(state => state.entrySlice.entrySliceOpenState);
    const closeModalWindow = useSelector(state => state.entrySlice.entrySliceOpenState);

    const dispatch = useDispatch();

    return (
        <>
            <button className={styles.button} onClick={() => dispatch(entrySliceOpen())}>{props.text}</button>
            {openModalWindow && <Modal onClose={() => dispatch(entrySliceClose())}/>}
            {!openModalWindow && <Modal onClose={() => dispatch(entrySliceClose())}/>}

        </>
    );
}

export default Enrol;
