import React, { useEffect } from "react";

const HeavyTextGray = (props) => {
    const fontSize = props.fontSize || "30px";
    const color = props.color || "white";
    const className = props.className || "HeavyTextGray";
    const margin = props.margin || "0px";

    useEffect(() => {
        if (window.innerWidth <= "1500px") {
        fontSize = props.fontSize - "5px" || "30px" - "5px";;
        margin = props.margin - "2px" || "0px";
        console.log(window.innerWidth);
    }
    }, [fontSize, margin])






    return (
        <p
            className={className}
            style={{
                fontSize: fontSize,
                color: color,
                margin: margin
            }}
        >{props.text}</p>
    );
}

export default HeavyTextGray;
