import React, { useEffect, useState } from 'react';
import styles from "./Error.module.css";

const ErrorPopup = (props) => {
  const [show, setShow] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setShow(false), props.timeOut);
    return () => clearTimeout(timer);
  }, [props.timeOut]);

  return (
    <div className={`${styles.popup} ${styles.error} ${show ? styles.show : styles.hide}`}>
      <div className={styles.popupContent}>
        <div className={styles.message}>Ошибка!<br />
          {props.message || ""}
        </div>
        <button className={styles.closeButton} onClick={() => setShow(false)}>
          <box-icon name='x' color="#fff" />
        </button>
      </div>
    </div>
  );
};

export default ErrorPopup;
