import React from 'react';
import styles from './Modal.module.css';

const Modal = ({ onClose, children }) => {
    return (
        <div className={styles.zModalOverlay} onClick={onClose}>
            <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
                <button className={styles.closeButton} onClick={onClose}>×</button>
                <box-icon name='x' color = "#FF5C00" className = {styles.closeButton} onClick={onClose}/>
               <p className="textBlackBig">ЗАПИСЬ</p>
                <div className={styles.tabContainer}>
                    <button className={`${styles.tabButton} ${styles.activeTab}`}>ЗАКАЗАТЬ ЗВОНОК</button>
                    <button className={styles.tabButton}>НАПИСАТЬ НАМ</button>
                </div>
                {children}

                <form className={styles.form}>
                    <input placeholder="ВВЕДИТЕ ИМЯ" type="text" name="text" className="input"/>
                    <input type="text" placeholder="ВВЕДИТЕ НОМЕР ТЕЛЕФОНА*" className={styles.input} />
                    <div className={styles.privacyContainer}>
                        <input type="checkbox" id="privacy" className={styles.checkbox} />
                        <label htmlFor="privacy" className={styles.privacyText}>согласие с политикой конфиденциальности</label>
                    </div>
                    <button type="submit" className={styles.submitButton}>ОТПРАВИТЬ ЗАЯВКУ</button>
                </form>
            </div>
        </div>
    );
};

export default Modal;
