import React, { useEffect, useState } from 'react';
import styles from "./Error.module.css";

const SuccessPopup = (props) => {
  const [show, setShow] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setShow(false), props.timeOut);
    return () => clearTimeout(timer);
  }, [props.timeOut]);

  return (
    <div className={`${styles.popup} ${styles.success} ${show ? styles.show : styles.hide}`}>
      <div className={styles.popupContent}>
        <div className={styles.message}>Успех!<br/>
            {props.message || ""}
        </div>
        <box-icon name='x' color="#fff" className={styles.closeButton} onClick={() => setShow(false)} />
      </div>
    </div>
  );
};

export default SuccessPopup;
