import { Link } from "react-router-dom/cjs/react-router-dom.min";
import styles from "./FormForCall.module.css";
import { useRef, useState } from "react";
import ErrorPopup from "../ErrorGroup/ErrorPopup";

const FormForCall = ({ display }) => {
    const [isValid, setIsValid] = useState(null);
    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [isChecked, setIsChecked] = useState(false);  // Установлено начальное значение false
    const [errorMessage, setErrorMessage] = useState("");  // Добавлено для хранения сообщений об ошибках

    const refName = useRef();
    const refPhone = useRef();

    const sentDatahandler = (e) => {
        e.preventDefault();

        const currentName = refName.current.value;
        const currentPhone = refPhone.current.value;

        setName(currentName);
        setPhone(currentPhone);

        let isFormValid = true;
        let message = "";

        if (currentName.trim() === "") {
            message = "Неизвестный пользователь";
            isFormValid = false;
        }

        if (currentPhone.trim().length === 0) {
            message = "Введите номер телефона";
            isFormValid = false;
        }

        if (!isChecked) {
            message = "Согласитесь с политикой конфиденциальности";
            isFormValid = false;
        }

        setIsValid(isFormValid);
        setErrorMessage(message);

        if (isFormValid) {
            console.log(currentName, currentPhone, "Пользователь согласился");
        }
    };

    const handleCheckboxChange = (event) => {
        setIsChecked(event.target.checked);
    };

    return (
        <>
            {errorMessage && <ErrorPopup timeOut="5000" message={errorMessage} />}
            <form className={styles.form} style={{ display: display }} onSubmit={sentDatahandler}>
                <input
                    placeholder="ВВЕДИТЕ ИМЯ"
                    type="text"
                    name="text"
                    className="input"
                    ref={refName}
                />

                <input
                    placeholder="ВВЕДИТЕ НОМЕР ТЕЛЕФОНА *"
                    type="number"
                    name="number"
                    className="input"
                    ref={refPhone}
                    onBlur={() => {
                        if (refPhone.current.value.trim() === '') {
                            setErrorMessage("Введите номер телефона");
                            setIsValid(false);
                        }
                    }}
                    style={{ borderColor: isValid === null ? "" : isValid ? "green" : "red" }}
                />

                <div className={styles.privacyContainer}>
                    <input
                        type="checkbox"
                        id="privacy"
                        checked={isChecked}
                        onChange={handleCheckboxChange}
                    />
                    <label htmlFor="privacy" className={`${styles.privacyText} ${!isChecked ? styles.error : ""}`}>
                        согласен с <Link to="/privacy-policy">
                            <span className={styles.spanColor}>политикой конфиденциальности *</span>
                        </Link>
                    </label>
                </div>

                <button type="submit" className={styles.submitButton} disabled={!isValid}>
                    <span className='textWhiteSmall'>ОТПРАВИТЬ ЗАЯВКУ</span>
                </button>
            </form>
        </>
    );
};

export default FormForCall;
