import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    entrySliceOpenState: false,
    entrySliceCloseState: false,
    changingTheLayout: false
};

export const entrySlice = createSlice({
    name: "togglePricesAndServices",
    initialState,
    reducers: {
        entrySliceOpen(state) {
            state.entrySliceOpenState = !state.entrySliceOpenState;
        },

        entrySliceClose(state) {
            state.entrySliceCloseState = !state.entrySliceCloseState;
        },

        changingTheLayout(state) {
            state.entrySliceCloseState = !state.entrySliceCloseState;
        },


    }
});

export const { toggleWindow } = entrySlice.actions;

export default entrySlice.reducer;
