import { Link } from "react-router-dom/cjs/react-router-dom.min";
import styles from "./FormForCall.module.css";
import { useRef, useState } from "react";

const FormForCall = ({ display }) => {
    const [isValid, setIsValid] = useState(false);

    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [isChecked, setIsChecked] = useState(undefined);


    const refName = useRef();
    const refPhone = useRef();

    const sentDatahandler = (e) => {
        e.preventDefault();

        setName(refName.current.value);
        setPhone(refName.current.value);

        if (name.trim() === "") {
            console.log("Неизвестный пользователь");
        }

        if (phone.trim() > 0) {
            setIsValid(false);
        }

        if (!isChecked) {
            setIsValid(false);
        }

        if (phone.trim() > 0 && isChecked) {
            setIsValid(true)
        }

    }



    return (
        <>
            <form className={styles.form} style={{ display: display }} onSubmit={sentDatahandler}>
                <input 
                placeholder="ВВЕДИТЕ ИМЯ" 
                type="text" name="text" 
                className="input" 
                ref={refName} 
                />

                <input placeholder="ВВЕДИТЕ НОМЕР ТЕЛЕФОНА *" type="number" name="number" className="input" ref={refPhone} style={{borderColor: isValid ? "green" : "red"}}/>

                <div className={styles.privacyContainer}>
                    <input type="checkbox" id="privacy" className={styles.checkbox} checked = {isChecked}/>
                    <label htmlFor="privacy" className={styles.privacyText}>согласен с <Link>
                        <span className={styles.spanColor}>политикой конфиденциальности *</span>
                    </Link>
                    </label>
                </div>

                <button type="submit" className={styles.submitButton}>
                    <span className='textWhiteSmall'>ОТПРАВИТЬ ЗАЯВКУ</span>
                </button>
            </form>
        </>
    )
}

export default FormForCall;
